import React, { Component } from 'react';
import { CardList } from './components/cardlist/cardlist.component.jsx';
import { Search } from './components/search/search.component'
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [],
      searchField: ""
    }
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users").then(res => res.json()).then(res => this.setState({monsters: res})).catch(err => console.log(err))
  }

  eventHandling(e) {
    this.setState({searchField: e.target.value})
  }

  render() {
    const { monsters , search } = this.state;
    const filtered = monsters.filter(mon => mon.toLowerCase().includes(search.toLowerCase()));

    return (
      <div className="App">
        <Search placeholder="Search Monster: " onChange={this.eventHandling}/>
        <CardList monsters={filtered}></CardList>
      </div>

    )
  }
}

export default App;
